import React,{useState} from 'react'
import '../Header.css';
import SendOutlinedIcon from '@material-ui/icons/SendOutlined';

function Chatscreen() {
const [input, setInput] = useState('')

    const [message, setmessage] = useState([
        {name:'Ranveer Kapoor',
        image:'data:image/jpeg;base64,',
        message:'whatss upppp.......'
        },
        {name:'Ranveer Kapoor',
        image:'data:image/jpeg;base64,',
        message:'how r u.'
        },
        {name:'Ranveer Kapoor',
        image:'data:image/jpeg;base64,',
        message:'great day'
        },
        {name:'Ranveer Kapoor',
        image:'data:image/jpeg;base64,',
        message:'love you...'
        }

        ,{
            message:'aalo bhata gobhi'
        }
    ])

    const handsend= e =>{
        e.preventDefault();

        setmessage([...message,{message:input}]);
        setInput('');
    }

    return (
        <div>


        <div className='chatscreen_master'>
    <p className='chatscreen_top_text'>You got love of your life</p>
    {
        message.map((message)=>(
            message.name?(
                <div className="Chatscreen_message">
                    <img src={message.image} 
                alt={message.name}
                 className="Chat_images chatcustom"/>
                <p  className="Chatscreen_text">{message.message}</p>
            </div>
            ):(
                <div className="Chatscreen_message">
                
               <p  className="Chatscreen_textUser">{message.message}</p>
           </div>
            )

          
        ))
    }
</div>


<div>
<form  className="chatscreen_input">
            <input type='text'
            value={input}
            on onChange={(e)=> setInput(e.target.value)}
             className="chatscreen_inputField"
              placeholder="enter your message"/>
              
            <button  
onClick={handsend}
ClassName="chatscreen_input_button"
            type="submit">

<SendOutlinedIcon fontSize="large"></SendOutlinedIcon>


            </button>
    
        
        </form>



</div>
       



    </div>
     
    )
}

export default Chatscreen
